var eradikalApp=angular.module("eradikalApp",["ngRoute"]);eradikalApp.controller("navCtrl",["$scope",function(t){t.btNav=function(e){e.stopPropagation(),t.navOpen=!t.navOpen}}]),eradikalApp.controller("playCtrl",["$scope","$http",function(t,e){t.btPlay=function(){t.playOpen=!t.playOpen}}]),eradikalApp.controller("viewCtrl",["$scope","$element",function(t,e){t.$on("$routeChangeStart",function(){e.addClass("loading")}),t.$on("$routeChangeSuccess",function(){setTimeout(function(){e.removeClass("loading")},600)})}]),eradikalApp.controller("page1",["$scope","$http",function(t,e){e.get("data/page1.json").then(function(e){console.log(e),t.data=e.data},function(t){})}]),eradikalApp.config(["$routeProvider",function(t){t.when("/test",{templateUrl:"partials/test.html",controller:"page1"}).when("/test2",{templateUrl:"partials/test2.html"}).otherwise({redirectTo:"/test"})}]);